from Crypto.Util.number import getPrime, bytes_to_long

m = bytes_to_long(open("flag.txt", "rb").read())
p, q = getPrime(512), getPrime(512)
n = p*q
c = pow(m, 0x10001, n)
a0, a1, b0, b1 = getPrime(64), getPrime(64), getPrime(64), getPrime(64)
X = a0 * p + b0
Y = a1 * p + b1

print(f'{n = }')
print(f'{c = }')
print(f'{X = }')
print(f'{Y = }')

